// Validar inputs
const categoria = ($json.categoria || '').toLowerCase().trim();
const entidadMedicaId = Number($json.entidad_medica_id);
const token = $json.token;
const page = Number($json.page || 1);
const session_id = $json.session_id || 'unknown';

if (!categoria) { throw new Error('No se proporcionó categoría'); }
if (!entidadMedicaId || isNaN(entidadMedicaId)) { throw new Error('No se proporcionó entidad_medica_id válido'); }
if (!token) { throw new Error('Token JWT es requerido'); }
if (!Number.isFinite(page) || page < 1) { throw new Error('page inválido'); }
if (!/^https?:\/\//.test($('CONFIG').first().json.BACKEND_NGROK_URL)) {
  throw new Error('BACKEND_NGROK_URL debe empezar con http:// o https://');
}

return { json: { categoria, entidad_medica_id: entidadMedicaId, token, page, session_id } };

// ===== FORMATEAR, FILTRAR Y PAGINAR CITAS (SOLO DISPONIBLES/FUTURAS) =====

const response = $json; // item actual
const page = Number($('Validar y Preparar').first().json.page || 1);
const pageSize = 10;

let citasArray = [];

// 1) Si response es un array, puede ser:
//    a) Array de objetos cita directamente -> tomar response
//    b) Array con 1 elemento que es el wrapper { success, citas: [...] } -> tomar elemento.citas
if (Array.isArray(response)) {
  // si es array de objetos cita (ej: [{slot_id:...}, {slot_id:...}])
  const looksLikeCitas = response.length > 0 && response.every(it => it && (it.slot_id || it.fecha || it.hora));
  if (looksLikeCitas) {
    citasArray = response;
  } else if (response.length === 1 && response[0] && typeof response[0] === 'object') {
    // si es un array con un wrapper: [{ success:true, citas: [...] }]
    const wrapper = response[0];
    if (Array.isArray(wrapper.citas)) {
      citasArray = wrapper.citas;
    } else if (Array.isArray(wrapper.data)) {
      citasArray = wrapper.data;
    } else if (Array.isArray(wrapper.results)) {
      citasArray = wrapper.results;
    } else {
      // como fallback, intentar usar directamente el wrapper (por si viene un único objeto cita dentro)
      citasArray = [wrapper];
    }
  } else {
    // fallback: dejar vacío (no reconocido)
    citasArray = [];
  }
} else if (response && typeof response === 'object') {
  // 2) Si response es un objeto wrapper
  if (Array.isArray(response.citas)) {
    citasArray = response.citas;
  } else if (Array.isArray(response.results)) {
    citasArray = response.results;
  } else if (Array.isArray(response.data)) {
    citasArray = response.data;
  } else {
    // tal vez el objeto ya es una cita única
    const looksLikeCita = response && (response.slot_id || response.fecha || response.hora);
    citasArray = looksLikeCita ? [response] : [];
  }
}

// DEBUG (opcional): ver qué se recibió
// console.log('Citas detectadas count:', citasArray.length, JSON.stringify(citasArray.slice(0,2), null, 2));

// Mapear/normalizar y filtrar disponibles
const normalizadas = citasArray
  .map((cita) => ({
    slot_id: cita.id ?? cita.slot_id ?? cita.pk ?? null,
    agenda_id: cita.agenda_id ?? cita.agenda ?? null,
    medico_id: cita.medico_id ?? cita.doctor_id ?? null,
    fecha: cita.fecha ?? cita.fecha_formateada ?? '',
    hora: cita.hora ?? cita.hora_cita ?? '',
    medico: cita.medico_nombre ?? cita.doctor_nombre ?? cita.doctor ?? cita.medico ?? '',
    especialidad: cita.especialidad_nombre ?? cita.specialty ?? cita.especialidad ?? '',
    consultorio: cita.consultorio ?? '',
    disponible: cita.disponible !== false
  }))
  .filter((c) => c.disponible === true && c.slot_id);

// Excluir pasadas (fecha/hora ISO)
const now = new Date();
const yyyy = now.getFullYear();
const mm = String(now.getMonth()+1).padStart(2,'0');
const dd = String(now.getDate()).padStart(2,'0');
const HH = String(now.getHours()).padStart(2,'0');
const MM = String(now.getMinutes()).padStart(2,'0');
const SS = String(now.getSeconds()).padStart(2,'0');
const today = `${yyyy}-${mm}-${dd}`;
const currentTime = `${HH}:${MM}:${SS}`;

const futuras = normalizadas.filter((c) => {
  if (!c.fecha) return false;
  if (c.fecha > today) return true;
  if (c.fecha < today) return false;
  if (!c.hora) return false;
  return c.hora >= currentTime;
});

// Orden estable por fecha+hora
futuras.sort((a,b) => (a.fecha + ' ' + a.hora).localeCompare(b.fecha + ' ' + b.hora));

const total = futuras.length;
if (total === 0) {
  return { json: { success: false, mensaje: 'No hay citas disponibles', citas: [], total_citas: 0, page, has_more: false } };
}

// Paginación 10 en 10
const start = (page - 1) * pageSize;
const end = start + pageSize;
const pagina = futuras.slice(start, end);

// Construir mapeo posición → slot_id (1..10) para el Agente
const lista_candidatos = pagina.map((c) => ({ slot_id: c.slot_id, agenda_id: c.agenda_id, fecha: c.fecha, hora: c.hora, medico: c.medico, especialidad: c.especialidad }));
const mapa_posiciones = {};
for (let i=0;i<lista_candidatos.length;i++){ mapa_posiciones[String(i+1)] = lista_candidatos[i]; }

return { json: { success: true, citas: lista_candidatos, total_citas: total, page, has_more: end < total, lista_candidatos, mapa_posiciones } };

parameters.jsCode:
const CONFIG = {
  BACKEND_NGROK_URL: "https://5e3708b60e02.ngrok-free.app",
  NGROK_HEADER_NAME: "ngrok-skip-browser-warning",
  NGROK_HEADER_VALUE: "true",
  N8N_WEBHOOK_SECRET: "TuSecretoSuperSeguro123",
  TELEFONO_CLINICA: "+573001234567"
};

console.log('=== CONFIG CARGADA ===');
console.log('BACKEND_NGROK_URL:', CONFIG.BACKEND_NGROK_URL);

return {
  json: {
    ...CONFIG,
    ...$json
  }
};

// ===== CREAR MAPEO POSICIÓN → SLOT_ID =====
const input = $input.all()[0].json;
const sessionId = input.session_id || $('When Executed by Another Workflow').first().json.session_id;

console.log('=== CREAR MAPEO ===');
console.log('Input:', JSON.stringify(input, null, 2));

// Obtener las citas del resultado
let citas = [];
if (input.citas && Array.isArray(input.citas)) {
    citas = input.citas;
} else if (input.resultado && input.resultado.citas) {
    citas = input.resultado.citas;
} else if (Array.isArray(input)) {
    citas = input;
}

console.log('Total citas encontradas:', citas.length);

// Crear mapeo: posición (1-10) → slot_id
const mapeo = {};
citas.slice(0, 10).forEach((cita, index) => {
    const posicion = index + 1;
    mapeo[posicion] = cita.slot_id;
    console.log(`Posición ${posicion} → Slot ID ${cita.slot_id} (${cita.medico_nombre})`);
});

// Preparar para guardar en Redis
const resultado = {
    key: `sophia:mapeo:${sessionId}`,
    value: JSON.stringify(mapeo),
    ttl: 3600,  // 1 hora
    session_id: sessionId,
    mapeo_preview: mapeo,
    original_data: input  // Pasar los datos originales
};

console.log('Mapeo creado:', mapeo);
console.log('Key Redis:', resultado.key);

return { json: resultado };

// Retornar los datos originales para el Agent
const input = $input.all()[0].json;
return { json: input.original_data };
