// Preparar contexto actualizado
const conversacionId = Number($json.conversacion_id);
const token = $json.token;

if (!conversacionId || isNaN(conversacionId)) {
    throw new Error('conversacion_id inválido');
}

if (!token) {
    throw new Error('Token JWT es requerido');
}

// Construir contexto con campos individuales (como espera el backend)
const sintomasReportados = $json.sintomas_reportados || "";
const categoriaIdentificada = $json.categoria_identificada || "";
const flujoActual = $json.estado_flujo || "";

// Log estructurado
console.log(JSON.stringify({
    level: 'INFO',
    timestamp: new Date().toISOString(),
    operation: 'ACTUALIZAR_CONTEXTO',
    conversacion_id: conversacionId,
    sintomas_reportados: sintomasReportados,
    categoria_identificada: categoriaIdentificada,
    flujo_actual: flujoActual
}));

return {
    json: {
        conversacion_id: conversacionId,
        sintomas_reportados: sintomasReportados,
        categoria_identificada: categoriaIdentificada,
        flujo_actual: flujoActual,
        token: token
    }
};

// Log de respuesta
const success = $json.success !== false;

console.log(JSON.stringify({
    level: success ? 'INFO' : 'ERROR',
    timestamp: new Date().toISOString(),
    operation: 'ACTUALIZAR_CONTEXTO_RESPONSE',
    success,
    conversacion_id: $('Preparar Contexto').item.json.conversacion_id,
    response: $json
}));

return {
    json: {
        success: success,
        mensaje: success ? 'Contexto actualizado correctamente' : 'Error al actualizar contexto',
        conversacion_id: $('Preparar Contexto').item.json.conversacion_id
    }
};
