// Validar inputs
const categoria = ($json.categoria || '').toLowerCase().trim();
const entidadMedicaId = Number($json.entidad_medica_id);
const token = $json.token;
const page = Number($json.page || 1);
const session_id = $json.session_id || 'unknown';

if (!categoria) { throw new Error('No se proporcion√≥ categor√≠a'); }
if (!entidadMedicaId || isNaN(entidadMedicaId)) { throw new Error('No se proporcion√≥ entidad_medica_id v√°lido'); }
if (!token) { throw new Error('Token JWT es requerido'); }
if (!Number.isFinite(page) || page < 1) { throw new Error('page inv√°lido'); }

return { json: { categoria, entidad_medica_id: entidadMedicaId, token, page, session_id } };

// ===== FORMATEAR, FILTRAR Y PAGINAR CITAS (SOLO DISPONIBLES/FUTURAS) =====

const response = $json;
const page = Number($('Validar y Preparar').first().json.page || 1);
const pageSize = 10;

// Detectar arreglo de citas
let citasArray = [];
if (response.citas && Array.isArray(response.citas)) citasArray = response.citas;
else if (response.results && Array.isArray(response.results)) citasArray = response.results;
else if (Array.isArray(response)) citasArray = response;
else if (response.data && Array.isArray(response.data)) citasArray = response.data;

// Mapear normalizadas y filtrar disponibles
const normalizadas = citasArray
  .map((cita) => ({
    slot_id: cita.id ?? cita.slot_id ?? cita.pk ?? null,
    agenda_id: cita.agenda_id ?? cita.agenda ?? null,
    medico_id: cita.medico_id ?? cita.doctor_id ?? null,
    fecha: cita.fecha ?? cita.fecha_formateada ?? '',
    hora: cita.hora ?? cita.hora_cita ?? '',
    medico: cita.medico_nombre ?? cita.doctor_nombre ?? cita.doctor ?? cita.medico ?? '',
    especialidad: cita.especialidad_nombre ?? cita.specialty ?? cita.especialidad ?? '',
    consultorio: cita.consultorio ?? '',
    disponible: cita.disponible !== false
  }))
  .filter((c) => c.disponible === true && c.slot_id);

// Excluir pasadas (fecha/hora ISO)
const now = new Date();
const yyyy = now.getFullYear();
const mm = String(now.getMonth()+1).padStart(2,'0');
const dd = String(now.getDate()).padStart(2,'0');
const HH = String(now.getHours()).padStart(2,'0');
const MM = String(now.getMinutes()).padStart(2,'0');
const SS = String(now.getSeconds()).padStart(2,'0');
const today = `${yyyy}-${mm}-${dd}`;
const currentTime = `${HH}:${MM}:${SS}`;

const futuras = normalizadas.filter((c) => {
  if (!c.fecha) return false;
  if (c.fecha > today) return true;
  if (c.fecha < today) return false;
  if (!c.hora) return false;
  return c.hora >= currentTime;
});

// Orden estable por fecha+hora
futuras.sort((a,b) => (a.fecha + ' ' + a.hora).localeCompare(b.fecha + ' ' + b.hora));

const total = futuras.length;
if (total === 0) {
  return { json: { success: false, mensaje: 'No hay citas disponibles', citas: [], total_citas: 0, page, has_more: false } };
}

// Paginaci√≥n 10 en 10
const start = (page - 1) * pageSize;
const end = start + pageSize;
const pagina = futuras.slice(start, end);

// Construir mapeo posici√≥n ‚Üí slot_id (1..10) para el Agente
const lista_candidatos = pagina.map((c) => ({ slot_id: c.slot_id, agenda_id: c.agenda_id, fecha: c.fecha, hora: c.hora, medico: c.medico, especialidad: c.especialidad }));
const mapa_posiciones = {};
for (let i=0;i<lista_candidatos.length;i++){ mapa_posiciones[String(i+1)] = lista_candidatos[i]; }

return { json: { success: true, citas: lista_candidatos, total_citas: total, page, has_more: end < total, lista_candidatos, mapa_posiciones } };

// ===== GUARDAR MAPEO DE SLOTS EN REDIS =====
// CR√çTICO: Este mapeo permite que el flujo 06 sepa qu√© slot_id corresponde a cada posici√≥n

const items = $input.all();
const citas = items[0].json.citas || [];
const session_id = $('Validar y Preparar').first().json.session_id;

if (!session_id || session_id === 'unknown') {
  console.log('‚ö†Ô∏è No se puede guardar mapeo: session_id no disponible');
  return items;
}

if (citas.length === 0) {
  console.log('‚ö†Ô∏è No hay citas para mapear');
  return items;
}

// Crear mapeo: posici√≥n ‚Üí slot_id
const mapa = {};
citas.slice(0, 10).forEach((cita, index) => {
  const posicion = String(index + 1);
  mapa[posicion] = {
    slot_id: cita.slot_id,
    agenda_id: cita.agenda_id,
    fecha: cita.fecha,
    hora: cita.hora,
    medico: cita.medico,
    especialidad: cita.especialidad
  };
});

// Conectar a Redis
const Redis = require('ioredis');
const redis = new Redis({
  host: 'redis',
  port: 6379,
  db: 0
});

// Guardar en Redis (TTL: 10 minutos = 600 segundos)
const mapKey = `sophia:slot_map:${session_id}`;

try {
  await redis.setex(mapKey, 600, JSON.stringify(mapa));
  console.log('‚úÖ Mapeo guardado en Redis:', mapKey);
  console.log('üìä Total slots mapeados:', Object.keys(mapa).length);
  console.log('üîë Ejemplo mapeo:', JSON.stringify(mapa['1'], null, 2));
} catch (error) {
  console.error('‚ùå Error guardando mapeo en Redis:', error.message);
} finally {
  await redis.quit();
}

// Retornar datos originales + confirmaci√≥n
return items.map(item => ({
  json: {
    ...item.json,
    mapeo_guardado: true,
    mapeo_key: mapKey,
    total_mapeados: Object.keys(mapa).length
  }
}));

