// Validar y preparar datos
const conversacionId = Number($json.conversacion_id);
const token = $json.token;
const motivoCierre = $json.motivo_cierre || 'COMPLETADO';

if (!conversacionId || isNaN(conversacionId)) {
    throw new Error('conversacion_id inválido');
}

if (!token) {
    throw new Error('Token JWT es requerido');
}

// Log estructurado
console.log(JSON.stringify({
    level: 'INFO',
    timestamp: new Date().toISOString(),
    operation: 'FINALIZAR_CONVERSACION',
    conversacion_id: conversacionId,
    motivo_cierre: motivoCierre
}));

return {
    json: {
        conversacion_id: conversacionId,
        token: token,
        motivo_cierre: motivoCierre
    }
};

// Log de respuesta
const success = $json.success !== false;

console.log(JSON.stringify({
    level: success ? 'INFO' : 'WARN',
    timestamp: new Date().toISOString(),
    operation: 'FINALIZAR_CONVERSACION_RESPONSE',
    success,
    conversacion_id: $('Validar y Log').item.json.conversacion_id,
    response: $json
}));

return {
    json: {
        success: success,
        mensaje: success ? 'Conversación finalizada correctamente' : 'Error al finalizar conversación',
        conversacion_id: $('Validar y Log').item.json.conversacion_id
    }
};

